// Find data from each node by checking the structure
for (const item of inputData) {
  const data = item.json;

  // Check if this is logs data (has 'logs' property)
  if (data && data.logs !== undefined) {
    logsData = data;
  }

  // Check if this is total data (has 'total' property)
  if (data && data.total !== undefined) {
    totalData = data;
  }
}

// Check for errors - but allow empty results
if (logsData && logsData.error) {
  // If it's a table not found error, use empty logs
  const errorMsg = logsData.message || '';
  if (errorMsg.includes('does not exist') || errorMsg.includes('relation') || errorMsg.includes('42P01')) {
    logsData = { logs: [] };
  } else {
    // For other errors, still return error
    return { json: logsData };
  }
}

if (totalData && totalData.error) {
  // If it's a table not found error, use empty total
  const errorMsg = totalData.message || '';
  if (errorMsg.includes('does not exist') || errorMsg.includes('relation') || errorMsg.includes('42P01')) {
    totalData = { total: [{ total: '0' }] };
  } else {
    // For other errors, still return error
    return { json: totalData };
  }
}

// If we don't have both, use defaults (this is OK - queries might return empty)
if (!logsData) {
  logsData = { logs: [] };
}

if (!totalData) {
  totalData = { total: [{ total: '0' }] };
}

// Both queries succeeded (or have defaults), merge results
// Safety check - ensure logs and total are arrays
const logs = Array.isArray(logsData.logs) ? logsData.logs : (logsData.logs ? [logsData.logs] : []);
const total = Array.isArray(totalData.total) ? totalData.total : (totalData.total ? [totalData.total] : []);

return {
  json: {
    logs: logs,
    total: total
  }
};

// Manual UUID v4 generator
function generateUUIDv4() {
  const hex = '0123456789abcdef';
  let uuid = '';
  for (let i = 0; i < 36; i++) {
    if (i === 8 || i === 13 || i === 18 || i === 23) {
      uuid += '-';
    } else if (i === 14) {
      uuid += '4';
    } else if (i === 19) {
      uuid += hex[(Math.random() * 4 | 8)];
    } else {
      uuid += hex[Math.random() * 16 | 0];
    }
  }
  return uuid;
}

// Format response
const mergedData = $('Merge Results').item.json;

// Safety check - if there's an error, don't process
if (mergedData.error) {
  return {
    json: {
      error: 'database_error',
      status: 500,
      message: mergedData.message || 'Database error occurred'
    }
  };
}

const logs = Array.isArray(mergedData.logs) ? mergedData.logs : [];
const totalArray = Array.isArray(mergedData.total) ? mergedData.total : [];
// Extract total count - PostgreSQL COUNT returns { total: '123' } or { count: '123' }
let total = 0;
if (totalArray.length > 0 && totalArray[0]) {
  const countData = totalArray[0];
  // Try different possible field names
  if (countData.total !== undefined) {
    total = parseInt(countData.total) || 0;
  } else if (countData.count !== undefined) {
    total = parseInt(countData.count) || 0;
  } else if (typeof countData === 'object' && Object.keys(countData).length > 0) {
    // Try to get first numeric value
    const firstValue = Object.values(countData)[0];
    total = parseInt(firstValue) || 0;
  }
}

// Get limit and offset from input data (passed through workflow)
// Try to get from Extract Data node, but fallback to defaults if not available
let limit = 100;
