  const extractData = $('Extract Data');
  if (extractData && extractData.item && extractData.item.json) {
    limit = extractData.item.json.limit || 100;
    offset = extractData.item.json.offset || 0;
  }
} catch (e) {
  // If Extract Data node is not available, use defaults
  limit = 100;
  offset = 0;
}

const formatted = logs.map(log => ({
  id: log.id,
  action: log.action || '',
  resourceType: log.resource_type || '',
  resourceId: log.resource_id || null,
  metadata: log.metadata || {},
  ip: log.ip || '',
