// Extract token and query params
const input = $input.item.json;
const headers = input.headers || {};
const query = input.query || {};

const authHeader = headers.authorization || headers.Authorization || '';

if (!authHeader || !authHeader.startsWith('Bearer ')) {
  return {
    json: {
      error: 'unauthorized',
      status: 401,
      message: 'Missing or invalid Authorization header'
    }
  };
}

const token = authHeader.replace('Bearer ', '').trim();
const limit = parseInt(query.limit) || 100;
const offset = parseInt(query.offset) || 0;
const action = query.action || null;
const userId = query.user_id || null;

return {
  json: {
    token: token,
    limit: limit,
    offset: offset,
    action: action,
    userId: userId
  }
};

// Handle both success and error cases from HTTP request
const httpResponse = $input.item.json;

// Check if this is an error response
if (httpResponse.error || httpResponse.statusCode >= 400) {
  return { json: { success: false, error: httpResponse.error?.message || 'Admin validation request failed' } };
}

const responseText = httpResponse.data;

let parsedData;
if (typeof responseText === 'string') {
  try {
    parsedData = JSON.parse(responseText);
  } catch (e) {
    return { json: { success: false, error: 'Failed to parse admin response' } };
  }
} else {
  parsedData = responseText;
}

if (!parsedData || Object.keys(parsedData).length === 0) {
  return { json: { success: false, error: 'Empty admin response' } };
}

if (parsedData.success === undefined) {
  return { json: { success: false, error: 'Invalid admin response format' } };
}

return { json: parsedData };

// Handle database errors for Get Logs
const logsResult = $input.item.json;

// Check if this is an error response (n8n error format)
if (logsResult.error) {
  return {
    json: {
      error: 'database_error',
      status: 500,
      message: logsResult.error.message || logsResult.error.toString() || 'Failed to fetch logs'
    }
  };
}

// Check if result is null or undefined
if (logsResult === null || logsResult === undefined) {
  return {
    json: {
      error: 'database_error',
      status: 500,
      message: 'Database returned null result'
    }
  };
}

// PostgreSQL node returns array directly when successful
if (!Array.isArray(logsResult)) {
  // Check if it's an error object
  if (logsResult.message || logsResult.code) {
    return {
      json: {
        error: 'database_error',
        status: 500,
        message: logsResult.message || 'Database query failed'
      }
    };
  }
  // If it's an object but not an array, wrap it
  return {
    json: {
      logs: [logsResult]
    }
  };
}

// Success case - return array
return {
  json: {
    logs: logsResult
  }
};

// Handle database errors for Get Total Count
const totalResult = $input.item.json;

// Check if this is an error response (n8n error format)
if (totalResult.error) {
  return {
    json: {
      error: 'database_error',
      status: 500,
      message: totalResult.error.message || totalResult.error.toString() || 'Failed to fetch total count'
    }
  };
}

// Check if result is null or undefined
if (totalResult === null || totalResult === undefined) {
  return {
    json: {
      error: 'database_error',
      status: 500,
      message: 'Database returned null result'
    }
  };
}

// PostgreSQL COUNT returns array with one row
if (!Array.isArray(totalResult)) {
  // Check if it's an error object
  if (totalResult.message || totalResult.code) {
    return {
      json: {
        error: 'database_error',
        status: 500,
        message: totalResult.message || 'Database query failed'
      }
    };
  }
  // If it's an object but not an array, wrap it
  return {
    json: {
      total: [totalResult]
    }
  };
}

// Success case - return array
return {
  json: {
    total: totalResult
  }
};

// Merge results from both queries
const logsData = $('Handle Logs Result').item.json;
const totalData = $('Handle Total Result').item.json;

// Check for errors
if (logsData.error) {
  return { json: logsData };
}

if (totalData.error) {
  return { json: totalData };
}

// Both queries succeeded, merge results
return {
  json: {
    logs: logsData.logs || [],
    total: totalData.total || []
  }
};

// Manual UUID v4 generator
function generateUUIDv4() {
  const hex = '0123456789abcdef';
  let uuid = '';
  for (let i = 0; i < 36; i++) {
    if (i === 8 || i === 13 || i === 18 || i === 23) {
      uuid += '-';
    } else if (i === 14) {
      uuid += '4';
    } else if (i === 19) {
      uuid += hex[(Math.random() * 4 | 8)];
    } else {
      uuid += hex[Math.random() * 16 | 0];
    }
  }
  return uuid;
}

// Format response
const mergedData = $('Merge Results').item.json;
const logs = mergedData.logs || [];
const total = mergedData.total && mergedData.total[0] ? mergedData.total[0].total : 0;

const formatted = logs.map(log => ({
  id: log.id,
  action: log.action || '',
  resourceType: log.resource_type || '',
  resourceId: log.resource_id || null,
  metadata: log.metadata || {},
  ip: log.ip || '',
  userAgent: log.user_agent || '',
  createdAt: log.created_at || null,
  user: log.username ? {
    username: log.username || '',
    firstName: log.first_name || '',
    lastName: log.last_name || ''
  } : null
}));

return {
  json: {
    success: true,
    data: formatted,
    pagination: {
      total: parseInt(total) || 0,
      limit: $('Extract Data').item.json.limit,
      offset: $('Extract Data').item.json.offset
    },
    traceId: generateUUIDv4()
  }
};
