// Extract token
const input = $input.item.json;
const headers = input.headers || {};

const authHeader = headers.authorization || headers.Authorization || '';

if (!authHeader || !authHeader.startsWith('Bearer ')) {
  return {
    json: {
      error: 'unauthorized',
      status: 401,
      message: 'Missing or invalid Authorization header'
    }
  };
}

const token = authHeader.replace('Bearer ', '').trim();

return {
  json: {
    token: token
  }
};

// Handle both success and error cases from HTTP request
const httpResponse = $input.item.json;

// Helper function to extract error message from various formats
function extractErrorMessage(error) {
  if (!error) return 'Admin validation request failed';

  // If it's a string, return it
  if (typeof error === 'string') {
    // Try to parse as JSON if it looks like JSON
    if (error.trim().startsWith('{') || error.trim().startsWith('[')) {
      try {
        const parsed = JSON.parse(error);
        if (parsed.message) return parsed.message;
        if (parsed.hint) return parsed.message + ': ' + parsed.hint;
      } catch (e) {
        // Not valid JSON, return as is
      }
    }
    return error;
  }

  // If it's an object
  if (typeof error === 'object') {
    // Check for nested message in error.message
    if (error.message) {
      // Try to parse message if it's a JSON string
      if (typeof error.message === 'string' && error.message.includes('{')) {
        try {
          const parsed = JSON.parse(error.message);
          if (parsed.message) return parsed.message;
          if (parsed.hint) return parsed.message + ': ' + parsed.hint;
        } catch (e) {
          // Not valid JSON, use message as is
        }
      }
      return error.message;
    }

    // Check for status code
    if (error.status === 404) {
      return 'Admin validation webhook not found. Please ensure the admin-validate workflow is active.';
    }

    if (error.status || error.statusCode) {
      return 'Admin validation request failed with status ' + (error.status || error.statusCode);
    }
  }

  return 'Admin validation request failed';
}

// Check if this is an error response
if (httpResponse.error) {
  const errorMsg = extractErrorMessage(httpResponse.error);
  return { 
    json: { 
      success: false, 
      error: errorMsg
    } 
  };
}

// Check for HTTP error status codes
if (httpResponse.statusCode && httpResponse.statusCode >= 400) {
  let errorMsg = 'Admin validation request failed with status ' + httpResponse.statusCode;

  // Try to extract message from response data
  if (httpResponse.data) {
    try {
      const dataStr = typeof httpResponse.data === 'string' ? httpResponse.data : JSON.stringify(httpResponse.data);
      if (dataStr.includes('not registered') || dataStr.includes('404')) {
        errorMsg = 'Admin validation webhook not found. Please ensure the admin-validate workflow is active.';
      } else {
        // Try to parse JSON from response
        const parsed = typeof httpResponse.data === 'string' ? JSON.parse(httpResponse.data) : httpResponse.data;
        if (parsed.message) errorMsg = parsed.message;
        if (parsed.hint) errorMsg = (parsed.message || errorMsg) + ': ' + parsed.hint;
      }
    } catch (e) {
      // Ignore parsing errors
    }
  }

  return { 
    json: { 
      success: false, 
      error: errorMsg
    } 
  };
}

// Check for status property (Axios format)
if (httpResponse.status && httpResponse.status >= 400) {
  let errorMsg = 'Admin validation request failed with status ' + httpResponse.status;

  if (httpResponse.data) {
    try {
      const dataStr = typeof httpResponse.data === 'string' ? httpResponse.data : JSON.stringify(httpResponse.data);
      if (dataStr.includes('not registered') || dataStr.includes('404')) {
        errorMsg = 'Admin validation webhook not found. Please ensure the admin-validate workflow is active.';
      }
    } catch (e) {
      // Ignore
    }
  }

  return { 
    json: { 
      success: false, 
      error: errorMsg
    } 
  };
}

const responseText = httpResponse.data;

let parsedData;
if (typeof responseText === 'string') {
  try {
    parsedData = JSON.parse(responseText);
  } catch (e) {
    return { json: { success: false, error: 'Failed to parse admin response' } };
  }
} else {
  parsedData = responseText;
}

if (!parsedData || Object.keys(parsedData).length === 0) {
  return { json: { success: false, error: 'Empty admin response' } };
}

if (parsedData.success === undefined) {
  return { json: { success: false, error: 'Invalid admin response format' } };
}

return { json: parsedData };

// Helper function to safely extract error message with full details
function getErrorMessage(error) {
  if (!error) return 'Unknown database error';

  // If it's a string, try to parse as JSON first
  if (typeof error === 'string') {
    // Check if it's a JSON string
    if (error.trim().startsWith('{') || error.trim().startsWith('[')) {
      try {
        const parsed = JSON.parse(error);
        return getErrorMessage(parsed);
      } catch (e) {
        // Not valid JSON, return as is
        return error;
      }
    }
    return error;
  }

  // If it's an object, build comprehensive error message
  if (typeof error === 'object') {
    const parts = [];

    // PostgreSQL error code
    if (error.code) parts.push('Code: ' + error.code);

    // Main error message
    if (error.message) parts.push(error.message);
    if (error.description && !error.message) {
      const desc = error.description;
      parts.push(desc);
    }

    // Detail (PostgreSQL specific)
    if (error.detail && error.detail !== error.message) parts.push('Detail: ' + error.detail);

    // Hint (PostgreSQL specific)
    if (error.hint) parts.push('Hint: ' + error.hint);

    // Where (PostgreSQL specific)
    if (error.where) parts.push('Where: ' + error.where);

    // Check for nested error objects
    if (error.error) {
      const nestedMsg = getErrorMessage(error.error);
      if (nestedMsg && nestedMsg !== 'Database query failed') {
        parts.push(nestedMsg);
      }
    }

    // If we have parts, join them
    if (parts.length > 0) {
      return parts.join(' | ');
    }

    // Try to stringify if it's an object with content
    try {
      const str = JSON.stringify(error, null, 2);
      if (str && str !== '{}' && str !== '[]') return str;
    } catch (e) {
      // Ignore JSON stringify errors
    }
  }

  // Last resort - try toString
  try {
    const str = error.toString();
    if (str && str !== '[object Object]') return str;
  } catch (e) {
    // Ignore
  }

  return 'Database query failed';
}

// Handle database errors - check all possible error formats in n8n
const inputData = $input.all();
const dbResult = inputData.length > 0 ? inputData[0].json : null;

// n8n error format when node fails with continueOnFail:
// Format 1: message at top level with nested error object
// Format 2: error object with nested message
// Format 3: error in json.error
// Format 4: Direct error object

// Check for n8n NodeOperationError format (message at top level)
if (dbResult && dbResult.message && dbResult.error) {
  const errorMsg = dbResult.message || getErrorMessage(dbResult.error);
  return {
    json: {
      error: 'database_error',
      status: 500,
      message: errorMsg
    }
  };
}

// Check for standard n8n error format
if (dbResult && dbResult.error) {
  const errorMsg = getErrorMessage(dbResult.error);
  return {
    json: {
      error: 'database_error',
      status: 500,
      message: errorMsg
    }
  };
}

// Check if the whole object is an error (sometimes n8n returns error directly)
if (dbResult && dbResult.json && dbResult.json.error) {
  const errorMsg = getErrorMessage(dbResult.json.error);
  return {
    json: {
      error: 'database_error',
      status: 500,
      message: errorMsg
    }
  };
}

// Check if there's a message at top level (n8n error format)
if (dbResult && dbResult.message && !dbResult.error) {
  return {
    json: {
      error: 'database_error',
      status: 500,
      message: dbResult.message
    }
  };
}

// Check if result is null or undefined
if (dbResult === null || dbResult === undefined) {
  return {
    json: {
      error: 'database_error',
      status: 500,
      message: 'Database returned null result'
    }
  };
}

// PostgreSQL node returns array directly when successful
if (!Array.isArray(dbResult)) {
  // Check if it's an error object (various formats)
  if (dbResult.message || dbResult.code || dbResult.error || dbResult.description || dbResult.detail || dbResult.hint) {
    const errorMsg = getErrorMessage(dbResult.error || dbResult);
    return {
      json: {
        error: 'database_error',
        status: 500,
        message: errorMsg
      }
    };
  }
  // If it's an object but not an array and not an error, wrap it
  return {
    json: {
      stats: [dbResult]
    }
  };
}

// Success case - return array
return {
  json: {
    stats: dbResult
  }
};

// Manual UUID v4 generator
function generateUUIDv4() {
  const hex = '0123456789abcdef';
  let uuid = '';
  for (let i = 0; i < 36; i++) {
    if (i === 8 || i === 13 || i === 18 || i === 23) {
      uuid += '-';
    } else if (i === 14) {
      uuid += '4';
    } else if (i === 19) {
      uuid += hex[(Math.random() * 4 | 8)];
    } else {
      uuid += hex[Math.random() * 16 | 0];
    }
  }
  return uuid;
}

// Format response
const dbData = $('Handle DB Result').item.json;

// Safety check - if there's an error, don't process
if (dbData.error) {
  return {
    json: {
      error: 'database_error',
      status: 500,
      message: dbData.message || 'Database error occurred'
    }
  };
}

// Safety check - if stats is missing or empty
if (!dbData.stats || !Array.isArray(dbData.stats) || dbData.stats.length === 0) {
  return {
    json: {
      success: true,
      data: {
        totalUsers: 0,
        activeUsers30d: 0,
        activeUsers7d: 0,
        totalCompanies: 0,
        totalChats: 0,
        totalMessages: 0,
        newUsers30d: 0,
        newCompanies30d: 0,
        activeSessions: 0
      },
      traceId: generateUUIDv4()
    }
  };
}

const stats = dbData.stats[0] || {};

return {
  json: {
    success: true,
    data: {
      totalUsers: parseInt(stats.total_users) || 0,
      activeUsers30d: parseInt(stats.active_users_30d) || 0,
      activeUsers7d: parseInt(stats.active_users_7d) || 0,
      totalCompanies: parseInt(stats.total_companies) || 0,
      totalChats: parseInt(stats.total_chats) || 0,
      totalMessages: parseInt(stats.total_messages) || 0,
      newUsers30d: parseInt(stats.new_users_30d) || 0,
      newCompanies30d: parseInt(stats.new_companies_30d) || 0,
      activeSessions: parseInt(stats.active_sessions) || 0
    },
    traceId: generateUUIDv4()
  }
};
