// Extract token and query params
const input = $input.item.json;
const headers = input.headers || {};
const query = input.query || {};

const authHeader = headers.authorization || headers.Authorization || '';

if (!authHeader || !authHeader.startsWith('Bearer ')) {
  return {
    json: {
      error: 'unauthorized',
      status: 401,
      message: 'Missing or invalid Authorization header'
    }
  };
}

const token = authHeader.replace('Bearer ', '').trim();
const userId = query.user_id || null;

return {
  json: {
    token: token,
    userId: userId
  }
};

// Handle both success and error cases from HTTP request
const httpResponse = $input.item.json;

// Check if this is an error response
if (httpResponse.error || httpResponse.statusCode >= 400) {
  return { json: { success: false, error: httpResponse.error?.message || 'Admin validation request failed' } };
}

const responseText = httpResponse.data;

let parsedData;
if (typeof responseText === 'string') {
  try {
    parsedData = JSON.parse(responseText);
  } catch (e) {
    return { json: { success: false, error: 'Failed to parse admin response' } };
  }
} else {
  parsedData = responseText;
}

if (!parsedData || Object.keys(parsedData).length === 0) {
  return { json: { success: false, error: 'Empty admin response' } };
}

if (parsedData.success === undefined) {
  return { json: { success: false, error: 'Invalid admin response format' } };
}

return { json: parsedData };

// Handle database errors
const dbResult = $input.item.json;

// Check if this is an error response (n8n error format)
if (dbResult.error) {
  return {
    json: {
      error: 'database_error',
      status: 500,
      message: dbResult.error.message || dbResult.error.toString() || 'Failed to fetch user limits'
    }
  };
}

// Check if result is null or undefined
if (dbResult === null || dbResult === undefined) {
  return {
    json: {
      error: 'database_error',
      status: 500,
      message: 'Database returned null result'
    }
  };
}

// PostgreSQL node returns array directly when successful
if (!Array.isArray(dbResult)) {
  // Check if it's an error object
  if (dbResult.message || dbResult.code) {
    return {
      json: {
        error: 'database_error',
        status: 500,
        message: dbResult.message || 'Database query failed'
      }
    };
  }
  // If it's an object but not an array, wrap it
  return {
    json: {
      limits: [dbResult]
    }
  };
}

// Success case - return array
return {
  json: {
    limits: dbResult
  }
};

// Manual UUID v4 generator
function generateUUIDv4() {
  const hex = '0123456789abcdef';
  let uuid = '';
  for (let i = 0; i < 36; i++) {
    if (i === 8 || i === 13 || i === 18 || i === 23) {
      uuid += '-';
    } else if (i === 14) {
      uuid += '4';
    } else if (i === 19) {
      uuid += hex[(Math.random() * 4 | 8)];
    } else {
      uuid += hex[Math.random() * 16 | 0];
    }
  }
  return uuid;
}

// Format response
const dbData = $('Handle DB Result').item.json;
const limits = dbData.limits || [];

const formatted = limits.map(l => ({
  id: l.id,
  userId: l.user_id,
  limitType: l.limit_type || '',
  limitValue: l.limit_value || 0,
  currentUsage: l.current_usage || 0,
  resetAt: l.reset_at || null,
  createdAt: l.created_at || null,
  updatedAt: l.updated_at || null,
  user: l.username ? {
    username: l.username || '',
    firstName: l.first_name || '',
    lastName: l.last_name || ''
  } : null
}));

return {
  json: {
    success: true,
    data: formatted,
    traceId: generateUUIDv4()
  }
};
