// Parse Telegram initData from webhook body
try {
  const body = $input.item.json.body || $input.item.json;
  const initData = body.initData || '';
  const appVersion = body.appVersion || '1.0.0';

  // Manual parse query string (URLSearchParams not available in n8n)
  const params = {};
  initData.split('&').forEach(pair => {
    const [key, value] = pair.split('=');
    params[key] = value;
  });

  // Parse user data
  const userStr = decodeURIComponent(params.user || '');
  const user = JSON.parse(userStr);

  return {
    json: {
      telegram_id: user.id.toString(),
      first_name: user.first_name,
      last_name: user.last_name || '',
      username: user.username || '',
      language_code: user.language_code || 'ru',
      app_version: appVersion,
      auth_date: params.auth_date
    }
  };
} catch (error) {
  // If parsing fails, return error that will stop workflow
  throw new Error('Invalid Telegram initData: ' + error.message);
}

// Generate UUID v4 (manual implementation)
function uuidv4() {
  return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
    const r = Math.random() * 16 | 0;
    const v = c === 'x' ? r : (r & 0x3 | 0x8);
    return v.toString(16);
  });
}

const sessionToken = uuidv4();

// Get user data (always present from Upsert User)
const userData = $('Upsert User').item.json;

// Try to get role data (might be empty)
const roleData = $('Get Role & Company').item?.json || {};

// Set default role if no company assigned
const role = roleData.role || 'viewer';
const companyId = roleData.company_id || null;

return {
  json: {
    session_token: sessionToken,
    user_id: userData.id,
    company_id: companyId,
    role: role
  }
};

// Build response from session data
const session = $input.item.json;

return {
  json: {
    success: true,
    data: {
      session_token: session.session_token,
      user: {
        id: session.user_id,
        role: session.role,
        company_id: session.company_id
      },
      expires_at: session.expires_at
    }
  }
};
