// Extract token from webhook body or Authorization header
const input = $input.item.json;
const body = input.body || input;
const headers = input.headers || {};

// Debug: log what we received
console.log('Webhook input:', JSON.stringify(input, null, 2));
console.log('Headers:', JSON.stringify(headers, null, 2));
console.log('Body:', JSON.stringify(body, null, 2));

// Try to get token from Authorization header first
// Headers can be lowercase or capitalized
const authHeader = headers.authorization || headers.Authorization || '';
let token = null;

if (authHeader) {
  console.log('Auth header found:', authHeader);
  if (authHeader.startsWith('Bearer ')) {
    token = authHeader.replace('Bearer ', '').trim();
  } else if (authHeader.startsWith('bearer ')) {
    token = authHeader.replace('bearer ', '').trim();
  } else {
    // Try to use header as-is if it doesn't have Bearer prefix
    token = authHeader.trim();
  }
} else if (body.token) {
  // Fallback to body.token for backward compatibility
  console.log('Using token from body');
  token = body.token;
}

if (!token) {
  console.error('No token found in request');
  throw new Error('Session token is required');
}

console.log('Token extracted successfully');
return {
  json: {
    token: token
  }
};

// Prepare error response for missing session
return {
  json: {
    success: false,
    error: 'unauthorized',
    status: 401,
    message: 'Invalid or expired token'
  }
};

// Check session validity
const session = $input.item.json;

const now = new Date();
const expiresAt = new Date(session.expires_at);
const lastActivity = new Date(session.last_activity_at);

// Check if session is expired
if (now > expiresAt) {
  return {
    json: {
      success: false,
      error: 'unauthorized',
      status: 401,
      message: 'Session has expired'
    }
  };
}

// Check grace window (24 hours of inactivity)
const hoursSinceActivity = (now - lastActivity) / (1000 * 60 * 60);
if (hoursSinceActivity > 24) {
  return {
    json: {
      success: false,
      error: 'unauthorized',
      status: 401,
      message: 'Session expired due to inactivity'
    }
  };
}

// Session is valid, pass through
return {
  json: session
};

// Prepare error response from validity check
const input = $input.item.json;
return {
  json: {
    success: false,
    error: input.error || 'unauthorized',
    status: input.status || 401,
    message: input.message || 'Invalid or expired token'
  }
};

// Build permissions based on role
// Get session data from Check Session Validity (passed through IF Validity OK)
// The IF node passes data through, so we can reference the previous node
const session = $('Check Session Validity').item.json;
const role = session.role;

const permissions = {
  owner: ['read', 'write', 'delete', 'manage_users', 'manage_company'],
  manager: ['read', 'write', 'delete'],
  viewer: ['read']
};

// Frontend expects role and companyId at data level, not nested in user
return {
  json: {
    success: true,
    data: {
      user: {
        id: session.user_id,
        telegram_id: session.telegram_id,
        first_name: session.first_name,
        last_name: session.last_name,
        username: session.username,
        company_name: session.company_name,
        permissions: permissions[role] || []
      },
      role: role,
      companyId: session.company_id,
      session: {
        expires_at: session.expires_at,
        last_activity_at: session.last_activity_at
      }
    }
  }
};
