// Extract token from Authorization header
const input = $input.item.json;
const headers = input.headers || {};

const authHeader = headers.authorization || headers.Authorization || '';

if (!authHeader || !authHeader.startsWith('Bearer ')) {
  return {
    json: {
      error: 'unauthorized',
      status: 401,
      message: 'Missing or invalid Authorization header'
    }
  };
}

const token = authHeader.replace('Bearer ', '').trim();

return {
  json: {
    token: token
  }
};

const httpResponse = $input.item.json;
const responseText = httpResponse.data;

let parsedData;
if (typeof responseText === 'string') {
  try {
    parsedData = JSON.parse(responseText);
  } catch (e) {
    return { json: { success: false, error: 'Failed to parse admin response' } };
  }
} else {
  parsedData = responseText;
}

if (!parsedData || Object.keys(parsedData).length === 0) {
  return { json: { success: false, error: 'Empty admin response' } };
}

if (parsedData.success === undefined) {
  return { json: { success: false, error: 'Invalid admin response format' } };
}

return { json: parsedData };

// Generate backup filename with timestamp
const now = new Date();
const timestamp = now.toISOString().replace(/[:.]/g, '-').slice(0, -5);
const backupFilename = `backup-${timestamp}.sql`;
const backupPath = `/var/backups/lumon/${backupFilename}`;

return {
  json: {
    backupFilename: backupFilename,
    backupPath: backupPath,
    timestamp: now.toISOString()
  }
};

// Manual UUID v4 generator
function generateUUIDv4() {
  const hex = '0123456789abcdef';
  let uuid = '';
  for (let i = 0; i < 36; i++) {
    if (i === 8 || i === 13 || i === 18 || i === 23) {
      uuid += '-';
    } else if (i === 14) {
      uuid += '4';
    } else if (i === 19) {
      uuid += hex[(Math.random() * 4 | 8)];
    } else {
      uuid += hex[Math.random() * 16 | 0];
    }
  }
  return uuid;
}

const backupData = $('Prepare Backup Data').item.json;
const commandResult = $input.item.json;
const adminData = $('Parse Admin Response').item.json.data;

// Parse JSON from script output
let backupInfo = { filename: backupData.backupFilename, file_path: backupData.backupPath, file_size: 0 };
if (commandResult.stdout) {
  try {
    const lines = commandResult.stdout.split('\n').filter(l => l.trim().startsWith('{'));
    if (lines.length > 0) {
      backupInfo = JSON.parse(lines[lines.length - 1]);
    }
  } catch (e) {
    // Use defaults
  }
}

return {
  json: {
    filename: backupInfo.filename || backupData.backupFilename,
    file_path: backupInfo.file_path || backupData.backupPath,
    file_size: backupInfo.file_size || 0,
    admin_user_id: adminData.admin_user_id,
    timestamp: backupData.timestamp
  }
};

