const backupRecord = $input.item.json;

return {
  json: {
    success: true,
    data: {
      id: backupRecord.id,
      filename: backupRecord.filename,
      file_path: backupRecord.file_path,
      file_size: backupRecord.file_size,
      created_at: backupRecord.created_at,
      status: backupRecord.status
    },
    traceId: generateUUIDv4()
  }
};

// Handle error from Create Backup
function generateUUIDv4() {
  const hex = '0123456789abcdef';
  let uuid = '';
  for (let i = 0; i < 36; i++) {
    if (i === 8 || i === 13 || i === 18 || i === 23) {
      uuid += '-';
    } else if (i === 14) {
      uuid += '4';
    } else if (i === 19) {
      uuid += hex[(Math.random() * 4 | 8)];
    } else {
      uuid += hex[Math.random() * 16 | 0];
    }
  }
  return uuid;
}

const error = $input.item.json;

return {
  json: {
    error: 'internal_error',
    status: 500,
    message: error.message || error.stderr || 'Failed to create backup',
    traceId: generateUUIDv4()
  }
};
