// Extract token from Authorization header
const input = $input.item.json;
const body = input.body || input;
const headers = input.headers || {};

// Debug: логируем все заголовки
console.log('[Extract Token] All headers:', JSON.stringify(headers, null, 2));
console.log('[Extract Token] Body:', JSON.stringify(body, null, 2));

// Проверяем все варианты регистра (n8n может нормализовать заголовки)
const authHeader = headers.authorization || headers.Authorization || headers['authorization'] || headers['Authorization'] || '';

console.log('[Extract Token] Auth header:', authHeader ? authHeader.substring(0, 20) + '...' : 'MISSING');

if (!authHeader || !authHeader.startsWith('Bearer ')) {
  console.error('[Extract Token] Missing or invalid Authorization header');
  return {
    json: {
      error: 'unauthorized',
      status: 401,
      message: 'Missing or invalid Authorization header',
      debug: {
        headersKeys: Object.keys(headers),
        hasAuth: !!authHeader,
        authHeaderStart: authHeader ? authHeader.substring(0, 20) : 'null'
      }
    }
  };
}

const token = authHeader.replace('Bearer ', '').trim();
const title = body.title || 'New Chat';

return {
  json: {
    token: token,
    title: title,
    ip: headers['x-forwarded-for'] || headers['x-real-ip'] || 'unknown',
    user_agent: headers['user-agent'] || 'unknown'
  }
};

const httpResponse = $input.item.json;
const responseText = httpResponse.data;

let parsedData;
if (typeof responseText === 'string') {
  try {
    parsedData = JSON.parse(responseText);
  } catch (e) {
    return { json: { success: false, error: 'Failed to parse auth response' } };
  }
} else {
  parsedData = responseText;
}

// Check if parsedData is empty or missing required fields
if (!parsedData || Object.keys(parsedData).length === 0) {
  return { json: { success: false, error: 'Empty auth response' } };
}

// Check if response has success field
if (parsedData.success === undefined) {
  return { json: { success: false, error: 'Invalid auth response format' } };
}

return { json: parsedData };

const authData = $('Parse Auth Response').item.json;
const titleNode = $('Extract Token').item.json;

const userId = authData?.data?.user?.id;
const companyId = authData?.data?.user?.company_id || null;
const title = titleNode?.title || 'New Chat';

if (!userId) {
  throw new Error('User ID not found in auth response');
}

return {
  json: {
    user_id: userId,
    company_id: companyId,
    title: title
  }
};

// Prepare audit event data
const authData = $('Parse Auth Response').item.json;
const chatData = $('Create Chat').item.json;
const extractData = $('Extract Token').item.json;

// Debug: проверяем наличие данных
if (!authData || !authData.data || !authData.data.user) {
  throw new Error('Auth data missing: ' + JSON.stringify(authData));
}

if (!chatData || !chatData.id) {
  throw new Error('Chat data missing: ' + JSON.stringify(chatData));
}

const userId = authData.data.user.id;
const chatId = chatData.id;
const title = extractData?.title || 'New Chat';
const ip = extractData?.ip || 'unknown';
const userAgent = extractData?.user_agent || 'unknown';

return {
  json: {
    user_id: userId,
    action: 'chat.created',
    resource_type: 'chat',
    resource_id: chatId,
    metadata: JSON.stringify({ title: title }),
    ip: ip,
    user_agent: userAgent
  }
};

// Check if audit logging failed
const input = $input.item.json;

// If this is an error response, log it but don't fail
if (input.error || input.message) {
  // Return error info for debugging, but don't throw
  return {
    json: {
      audit_log_error: true,
      error_message: input.error?.message || input.message || 'Unknown audit logging error',
      error_data: input
    }
  };
}

// Success case - audit event was logged
return {
  json: {
    audit_logged: true,
    audit_event_id: input.id || null
  }
};

// Manual UUID v4 generator
function generateUUIDv4() {
  const hex = '0123456789abcdef';
  let uuid = '';
  for (let i = 0; i < 36; i++) {
    if (i === 8 || i === 13 || i === 18 || i === 23) {
      uuid += '-';
    } else if (i === 14) {
      uuid += '4';
    } else if (i === 19) {
      uuid += hex[(Math.random() * 4 | 8)];
    } else {
      uuid += hex[Math.random() * 16 | 0];
    }
  }
  return uuid;
}

const chatData = $input.item.json;

return {
  json: {
    success: true,
    data: {
      id: chatData.id,
      user_id: chatData.user_id,
      company_id: chatData.company_id,
      title: chatData.title,
      created_at: chatData.created_at
    },
    traceId: generateUUIDv4()
  }
};

// Handle error from Create Chat
function generateUUIDv4() {
  const hex = '0123456789abcdef';
  let uuid = '';
  for (let i = 0; i < 36; i++) {
    if (i === 8 || i === 13 || i === 18 || i === 23) {
      uuid += '-';
    } else if (i === 14) {
      uuid += '4';
    } else if (i === 19) {
      uuid += hex[(Math.random() * 4 | 8)];
    } else {
      uuid += hex[Math.random() * 16 | 0];
    }
  }
  return uuid;
}

const error = $input.item.json;

return {
  json: {
    error: 'internal_error',
    status: 500,
    message: error.message || 'Failed to create chat',
    traceId: generateUUIDv4()
  }
};
