// Extract token from Authorization header
const input = $input.item.json;
const body = input.body || input;
const headers = input.headers || {};

const authHeader = headers.authorization || headers.Authorization || '';

if (!authHeader || !authHeader.startsWith('Bearer ')) {
  return {
    json: {
      error: 'unauthorized',
      status: 401,
      message: 'Missing or invalid Authorization header'
    }
  };
}

const token = authHeader.replace('Bearer ', '').trim();
const title = body.title || 'New Chat';

return {
  json: {
    token: token,
    title: title
  }
};

// Parse JSON from text response
const input = $input.item.json;
let responseText = input.data || input;

if (typeof responseText === 'string') {
  try {
    return { json: JSON.parse(responseText) };
  } catch (e) {
    return { json: { success: false, error: 'Failed to parse auth response' } };
  }
}

return { json: responseText };

// Prepare data for Create Chat
// Get data directly from Parse Auth Response (IF nodes don't pass data, only conditions)
const authData = $('Parse Auth Response').item.json;
const titleNode = $('Extract Token').item.json;

let userId = null;
let companyId = null;

// Path 1: authData.data.user.id (from auth.validate response)
if (authData?.data?.user?.id) {
  userId = authData.data.user.id;
  companyId = authData.data.user.company_id || null;
}
// Path 2: direct authData.user.id
else if (authData?.user?.id) {
  userId = authData.user.id;
  companyId = authData.user.company_id || null;
}
// Path 3: authData.user_id (direct property)
else if (authData?.user_id) {
  userId = authData.user_id;
  companyId = authData.company_id || null;
}

const title = titleNode?.title || 'New Chat';

if (!userId) {
  // Return full structure to see what we have
  throw new Error(JSON.stringify({
    error: 'User ID not found',
    authData: authData,
    titleNode: titleNode
  }, null, 2));
}

return {
  json: {
    user_id: userId,
    company_id: companyId,
    title: title
  }
};

// Manual UUID v4 generator
function generateUUIDv4() {
  const hex = '0123456789abcdef';
  let uuid = '';
  for (let i = 0; i < 36; i++) {
    if (i === 8 || i === 13 || i === 18 || i === 23) {
      uuid += '-';
    } else if (i === 14) {
      uuid += '4';
    } else if (i === 19) {
      uuid += hex[(Math.random() * 4 | 8)];
    } else {
      uuid += hex[Math.random() * 16 | 0];
    }
  }
  return uuid;
}

const chatData = $input.item.json;

return {
  json: {
    success: true,
    data: {
      id: chatData.id,
      user_id: chatData.user_id,
      company_id: chatData.company_id,
      title: chatData.title,
      created_at: chatData.created_at
    },
    traceId: generateUUIDv4()
  }
};

// Handle error from Create Chat
function generateUUIDv4() {
  const hex = '0123456789abcdef';
  let uuid = '';
  for (let i = 0; i < 36; i++) {
    if (i === 8 || i === 13 || i === 18 || i === 23) {
      uuid += '-';
    } else if (i === 14) {
      uuid += '4';
    } else if (i === 19) {
      uuid += hex[(Math.random() * 4 | 8)];
    } else {
      uuid += hex[Math.random() * 16 | 0];
    }
  }
  return uuid;
}

const error = $input.item.json;

return {
  json: {
    error: 'internal_error',
    status: 500,
    message: error.message || 'Failed to create chat',
    traceId: generateUUIDv4()
  }
};
