// Extract token from Authorization header, body, or query (fallback)
// Проблема: заголовок Authorization может не прийти в webhook, используем fallback
const input = $input.item.json;

// В n8n webhook body может быть в разных форматах:
// 1. input.body (если это объект)
// 2. input (если body уже распарсен)
// 3. JSON.parse(input.body) если body это строка
let body = input.body || input;

// Если body это строка, пытаемся распарсить
if (typeof body === 'string') {
  try {
    body = JSON.parse(body);
  } catch (e) {
    // Если не JSON, оставляем как есть
  }
}

// Если body все еще не объект, используем весь input
if (typeof body !== 'object' || body === null) {
  body = input;
}

const headers = input.headers || {};
const query = input.query || {};

// ПРИОРИТЕТ 1: Пробуем получить токен из body (для n8n webhooks это надежнее)
let authHeader = '';
let tokenSource = 'none';
if (body && typeof body === 'object') {
  if (body.session_token && typeof body.session_token === 'string' && body.session_token.trim().length > 0) {
    authHeader = `Bearer ${body.session_token.trim()}`;
    tokenSource = 'body.session_token';
  } else if (body.token && typeof body.token === 'string' && body.token.trim().length > 0) {
    authHeader = `Bearer ${body.token.trim()}`;
    tokenSource = 'body.token';
  } else if (body.authorization && typeof body.authorization === 'string' && body.authorization.trim().length > 0) {
    const authValue = body.authorization.trim();
    authHeader = authValue.startsWith('Bearer ') ? authValue : `Bearer ${authValue}`;
    tokenSource = 'body.authorization';
  }
}

// ПРИОРИТЕТ 2: Если токена нет в body, пробуем из заголовка Authorization
if (!authHeader || !authHeader.startsWith('Bearer ')) {
  const headerAuth = headers.authorization || headers.Authorization || headers['authorization'] || headers['Authorization'] || '';
  if (headerAuth && headerAuth.startsWith('Bearer ')) {
    authHeader = headerAuth;
    tokenSource = 'header.Authorization';
  }
}

// ПРИОРИТЕТ 3: Если токена нет в body и заголовке, пробуем из query параметров
if (!authHeader || !authHeader.startsWith('Bearer ')) {
  if (query.token && typeof query.token === 'string' && query.token.trim().length > 0) {
    authHeader = `Bearer ${query.token.trim()}`;
    tokenSource = 'query.token';
  } else if (query.session_token && typeof query.session_token === 'string' && query.session_token.trim().length > 0) {
    authHeader = `Bearer ${query.session_token.trim()}`;
    tokenSource = 'query.session_token';
  } else if (query.authorization && typeof query.authorization === 'string' && query.authorization.trim().length > 0) {
    const authValue = query.authorization.trim();
    authHeader = authValue.startsWith('Bearer ') ? authValue : `Bearer ${authValue}`;
    tokenSource = 'query.authorization';
  }
}

if (!authHeader || !authHeader.startsWith('Bearer ')) {
  return {
    json: {
      error: 'unauthorized',
      status: 401,
      message: 'Missing or invalid Authorization header or token. Please ensure you are logged in.',
      debug: {
        headersKeys: Object.keys(headers),
        hasAuth: !!authHeader,
        authHeaderStart: authHeader ? authHeader.substring(0, 20) : 'null',
        bodyType: typeof body,
        bodyIsObject: typeof body === 'object' && body !== null,
        bodyKeys: body && typeof body === 'object' ? Object.keys(body) : [],
        queryKeys: Object.keys(query),
        hasSessionToken: !!(body && typeof body === 'object' && body.session_token),
        hasToken: !!(body && typeof body === 'object' && body.token),
        hasAuthorization: !!(body && typeof body === 'object' && body.authorization),
        hasHeaderAuth: !!(headers.authorization || headers.Authorization),
        hasQueryToken: !!(query.token || query.session_token),
        tokenSource: tokenSource,
        inputKeys: Object.keys(input)
      }
    }
  };
}

const token = authHeader.replace('Bearer ', '').trim();
const title = (body && typeof body === 'object' && body.title) ? body.title : 'New Chat';

return {
  json: {
    token: token,
    title: title,
    tokenSource: tokenSource,
    ip: headers['x-forwarded-for'] || headers['x-real-ip'] || 'unknown',
    user_agent: headers['user-agent'] || 'unknown'
  }
};

// Parse JSON from text response (simplified version)
const input = $input.item.json;

// Check for HTTP request errors or status codes >= 400
if (input.error || (input.statusCode && input.statusCode >= 400)) {
  return { 
    json: { 
      success: false, 
      error: 'unauthorized', 
      status: input.statusCode || 401, 
      message: input.error?.message || input.message || 'Auth validation request failed' 
    } 
  };
}

// Get response text - HTTP Request with responseFormat: 'text' returns data as string
let responseText = input.data || input.body || input;

// If responseText is not a string, try to stringify
if (typeof responseText !== 'string') {
  if (typeof responseText === 'object' && responseText !== null) {
    responseText = JSON.stringify(responseText);
  } else {
    responseText = String(responseText);
  }
}

// If responseText is empty or just whitespace, return error
if (!responseText || responseText.trim().length === 0) {
  return { 
    json: { 
      success: false, 
      error: 'unauthorized', 
      status: input.statusCode || 401, 
      message: 'Empty auth response - token may be invalid or expired' 
    } 
  };
}

// Parse JSON
let parsedData;
try {
  parsedData = JSON.parse(responseText);
} catch (e) {
  return { 
    json: { 
      success: false, 
      error: 'unauthorized', 
      status: input.statusCode || 401, 
      message: 'Failed to parse auth response: ' + responseText.substring(0, 200) 
    } 
  };
}

// Check if response is valid
if (!parsedData || typeof parsedData !== 'object') {
  return { 
    json: { 
      success: false, 
      error: 'unauthorized', 
      status: input.statusCode || 401, 
      message: 'Invalid auth response format' 
    } 
  };
}

// Return parsed data (may be success or error)
return { json: parsedData };

const authData = $('Parse Auth Response').item.json;
const titleNode = $('Extract Token').item.json;

const userId = authData?.data?.user?.id;
const companyId = authData?.data?.user?.company_id || null;
const title = titleNode?.title || 'New Chat';

if (!userId) {
  throw new Error('User ID not found in auth response');
}

return {
  json: {
    user_id: userId,
    company_id: companyId,
    title: title
  }
};

