// Extract token from Authorization header (preferred), fallback to query/body
const input = $input.item.json;

const headers = input.headers || {};
const query = input.query || {};
let body = input.body || input;

// Parse body if it's a string
if (typeof body === 'string') {
  try {
    body = JSON.parse(body);
  } catch (e) {
    // If not JSON, leave as is
  }
}

if (typeof body !== 'object' || body === null) {
  body = input;
}

// PRIORITY 1: Authorization header (standard and most reliable)
let authHeader = headers.authorization || headers.Authorization || headers['authorization'] || headers['Authorization'] || '';

// PRIORITY 2: Query parameters (fallback for old clients)
if (!authHeader || !authHeader.startsWith('Bearer ')) {
  if (query.token && typeof query.token === 'string' && query.token.trim().length > 0) {
    authHeader = `Bearer ${query.token.trim()}`;
  } else if (query.session_token && typeof query.session_token === 'string' && query.session_token.trim().length > 0) {
    authHeader = `Bearer ${query.session_token.trim()}`;
  } else if (query.authorization && typeof query.authorization === 'string' && query.authorization.trim().length > 0) {
    const authValue = query.authorization.trim();
    authHeader = authValue.startsWith('Bearer ') ? authValue : `Bearer ${authValue}`;
  }
}

// PRIORITY 3: Body (fallback for POST requests)
if (!authHeader || !authHeader.startsWith('Bearer ')) {
  if (body && typeof body === 'object') {
    if (body.session_token && typeof body.session_token === 'string' && body.session_token.trim().length > 0) {
      authHeader = `Bearer ${body.session_token.trim()}`;
    } else if (body.token && typeof body.token === 'string' && body.token.trim().length > 0) {
      authHeader = `Bearer ${body.token.trim()}`;
    } else if (body.authorization && typeof body.authorization === 'string' && body.authorization.trim().length > 0) {
      const authValue = body.authorization.trim();
      authHeader = authValue.startsWith('Bearer ') ? authValue : `Bearer ${authValue}`;
    }
  }
}

if (!authHeader || !authHeader.startsWith('Bearer ')) {
  return {
    json: {
      error: 'unauthorized',
      status: 401,
      message: 'Missing or invalid Authorization header. Please ensure you are logged in.'
    }
  };
}

const token = authHeader.replace('Bearer ', '').trim();

return {
  json: {
    token: token
  }
};

// Handle both success and error cases from HTTP request
const httpResponse = $input.item.json;

// Check if this is an error response
if (httpResponse.error || httpResponse.statusCode >= 400) {
  return { json: { success: false, error: httpResponse.error?.message || 'Auth validation request failed' } };
}

const responseText = httpResponse.data;

let parsedData;
if (typeof responseText === 'string') {
  try {
    parsedData = JSON.parse(responseText);
  } catch (e) {
    return { json: { success: false, error: 'Failed to parse auth response' } };
  }
} else {
  parsedData = responseText;
}

if (!parsedData || Object.keys(parsedData).length === 0) {
  return { json: { success: false, error: 'Empty auth response' } };
}

if (parsedData.success === undefined) {
  return { json: { success: false, error: 'Invalid auth response format' } };
}

return { json: parsedData };

const authData = $('Parse Auth Response').item.json;

// Try different paths to find userId
let userId = null;
let companyId = null;

// Path 1: authData.data.user.id (expected based on auth.validate response)
if (authData && authData.data && authData.data.user && authData.data.user.id) {
  userId = authData.data.user.id;
  // companyId is at data level, not in user object
  companyId = authData.data.companyId || null;
}
// Path 2: authData.user.id (in case data wrapper is missing)
else if (authData && authData.user && authData.user.id) {
  userId = authData.user.id;
  companyId = authData.companyId || null;
}
// Path 3: authData.id (in case it's just the user object)
else if (authData && authData.id) {
  userId = authData.id;
  companyId = authData.company_id || authData.companyId || null;
}

if (!userId) {
  throw new Error('User ID not found in auth response. Auth data keys: ' + (authData ? Object.keys(authData).join(', ') : 'null'));
}

return {
  json: {
    user_id: userId,
    company_id: companyId
  }
};

// Manual UUID v4 generator
function generateUUIDv4() {
  const hex = '0123456789abcdef';
  let uuid = '';
  for (let i = 0; i < 36; i++) {
    if (i === 8 || i === 13 || i === 18 || i === 23) {
      uuid += '-';
    } else if (i === 14) {
      uuid += '4';
    } else if (i === 19) {
      uuid += hex[(Math.random() * 4 | 8)];
    } else {
      uuid += hex[Math.random() * 16 | 0];
    }
  }
  return uuid;
}

const chats = $input.all();

// Frontend expects snake_case field names for consistency
const formattedChats = chats.map(item => {
  const chat = item.json;
  return {
    id: chat.id,
    user_id: chat.user_id,
    title: chat.title || 'Без названия',
    created_at: chat.created_at,
    updated_at: chat.updated_at,
    messageCount: parseInt(chat.message_count) || 0,
    lastMessageAt: chat.last_message_at,
    lastMessage: chat.last_message || null
  };
});

return {
  json: {
    success: true,
    data: formattedChats,
    count: formattedChats.length,
    traceId: generateUUIDv4()
  }
};

// Handle error from Get Chats List
function generateUUIDv4() {
  const hex = '0123456789abcdef';
  let uuid = '';
  for (let i = 0; i < 36; i++) {
    if (i === 8 || i === 13 || i === 18 || i === 23) {
      uuid += '-';
    } else if (i === 14) {
      uuid += '4';
    } else if (i === 19) {
      uuid += hex[(Math.random() * 4 | 8)];
    } else {
      uuid += hex[Math.random() * 16 | 0];
    }
  }
  return uuid;
}

const error = $input.item.json;

return {
  json: {
    error: 'internal_error',
    status: 500,
    message: error.message || 'Failed to get chats list',
    traceId: generateUUIDv4()
  }
};
