// Extract token and service name
const input = $input.item.json;
const headers = input.headers || {};
const body = input.body || input;

const authHeader = headers.authorization || headers.Authorization || '';

if (!authHeader || !authHeader.startsWith('Bearer ')) {
  return {
    json: {
      error: 'unauthorized',
      status: 401,
      message: 'Missing or invalid Authorization header'
    }
  };
}

const token = authHeader.replace('Bearer ', '').trim();
const service = body.service || 'all';

return {
  json: {
    token: token,
    service: service
  }
};

const httpResponse = $input.item.json;
const responseText = httpResponse.data;

let parsedData;
if (typeof responseText === 'string') {
  try {
    parsedData = JSON.parse(responseText);
  } catch (e) {
    return { json: { success: false, error: 'Failed to parse admin response' } };
  }
} else {
  parsedData = responseText;
}

if (!parsedData || Object.keys(parsedData).length === 0) {
  return { json: { success: false, error: 'Empty admin response' } };
}

if (parsedData.success === undefined) {
  return { json: { success: false, error: 'Invalid admin response format' } };
}

return { json: parsedData };

const extractData = $('Extract Data').item.json;
const adminData = $('Parse Admin Response').item.json.data;

return {
  json: {
    service: extractData.service,
    admin_user_id: adminData.admin_user_id
  }
};

// Parse health check results
const checkResult = $input.item.json;
const prepareData = $('Prepare Check Data').item.json;

let results = [];

if (checkResult.stdout) {
  const lines = checkResult.stdout.split('\n').filter(line => line.trim());
  for (const line of lines) {
    try {
      const parsed = JSON.parse(line);
      results.push(parsed);
    } catch (e) {
      // Skip invalid JSON lines
    }
  }
}

// If no results, create default
if (results.length === 0) {
  const services = prepareData.service === 'all' ? ['n8n', 'postgresql', 'nginx', 'supabase-studio'] : [prepareData.service];
  for (const service of services) {
    results.push({
      service: service,
      status: 'unknown',
      error_message: checkResult.stderr || 'Health check failed'
    });
  }
}

return {
  json: {
    results: results,
    admin_user_id: prepareData.admin_user_id
  }
};

// Calculate overall status and extract system metrics
const results = $('Parse Results').item.json.results;

let overallStatus = 'healthy';
const servicesStatus = {};
let systemMetrics = null;

for (const result of results) {
  servicesStatus[result.service] = result.status;
  if (result.status === 'unhealthy') {
    overallStatus = 'down';
  } else if (result.status === 'degraded' && overallStatus === 'healthy') {
    overallStatus = 'degraded';
  }

  // Extract system metrics
  if (result.service === 'system' && result.metrics) {
    systemMetrics = result.metrics;
  }
}

return {
  json: {
    overall_status: overallStatus,
    services_status: servicesStatus,
    system_metrics: systemMetrics,
    results: results
  }
};

// Manual UUID v4 generator
function generateUUIDv4() {
  const hex = '0123456789abcdef';
  let uuid = '';
  for (let i = 0; i < 36; i++) {
    if (i === 8 || i === 13 || i === 18 || i === 23) {
      uuid += '-';
    } else if (i === 14) {
      uuid += '4';
    } else if (i === 19) {
      uuid += hex[(Math.random() * 4 | 8)];
    } else {
      uuid += hex[Math.random() * 16 | 0];
    }
  }
  return uuid;
}

const calculateStatus = $('Calculate Status').item.json;

return {
  json: {
    success: true,
    data: {
      overall_status: calculateStatus.overall_status,
      services_status: calculateStatus.services_status,
      results: calculateStatus.results
    },
    traceId: generateUUIDv4()
  }
};
