// Extract user_id and endpoint from input
const input = $input.item.json;

if (!input.user_id) {
  return {
    json: {
      error: 'bad_request',
      status: 400,
      message: 'Missing user_id for rate limiting',
      allowed: false
    }
  };
}

if (!input.endpoint) {
  return {
    json: {
      error: 'bad_request',
      status: 400,
      message: 'Missing endpoint for rate limiting',
      allowed: false
    }
  };
}

// Define rate limits per endpoint
const rateLimits = {
  'chat.create': 30,
  'chat.save-message': 30,
  'analytics': 100
};

const limit = rateLimits[input.endpoint] || 30; // Default to 30 req/min

return {
  json: {
    user_id: input.user_id,
    endpoint: input.endpoint,
    limit: limit,
    window_minutes: 1
  }
};

// Return error response
const error = $input.item.json;

return {
  json: {
    allowed: false,
    error: error.error,
    message: error.message,
    status: error.status
  }
};

// Check if rate limit exceeded
const params = $('Extract Params').item.json;
const result = $input.item.json;

const requestCount = parseInt(result.request_count) || 0;
const limit = params.limit;

if (requestCount >= limit) {
  return {
    json: {
      allowed: false,
      error: 'rate_limit_exceeded',
      message: `Rate limit exceeded: ${requestCount}/${limit} requests per minute`,
      status: 429,
      limit: limit,
      current: requestCount,
      retry_after: 60
    }
  };
}

return {
  json: {
    allowed: true,
    user_id: params.user_id,
    endpoint: params.endpoint,
    limit: limit,
    current: requestCount
  }
};

// Return rate limit exceeded
const error = $input.item.json;

return {
  json: {
    allowed: false,
    error: error.error,
    message: error.message,
    status: error.status,
    limit: error.limit,
    current: error.current,
    retry_after: error.retry_after
  }
};

